import logo from './logo.svg';
import './App.css';
import { Component, useState } from 'react';
import Success from "./success";

function App() {

  const [inputone,setinputone] = useState(0);
  const [inputtwo,setinputtwo] = useState(0);
  const [error,seterror] = useState(0);
  const [result,setresult] = useState(0);


  function caluclate(operation){

    if(inputone==NaN){
      seterror("input one is required");


    }
    else 
    if(inputtwo==NaN){
      seterror("input two is required");

    }
    else {
      console.log(operation);
      Number(inputone);
      Number(inputtwo);

      switch(operation){
        case "+": setresult(inputone+inputtwo);
        console.log("plus");
        break;
        case "-": setresult(inputone-inputtwo);
        console.log("sub");
        break;
        case "/": setresult(inputone/inputtwo);
        console.log("div");
        break;
        case "x": setresult(inputone*inputtwo);
        console.log("mul");
        break;
      }

    }

  }
  return (
    <>
    <div id="main">
            <input type="text" placeholder='Enter first number' onChange={(e)=>setinputone(e.target.value)} />
            <input type="text" placeholder='Enter second number' onChange={(e)=>setinputtwo(e.target.value)}/>
            <button onClick={()=>caluclate("+")}>+</button>
            <button onClick={()=>caluclate("-")}>-</button>
            <button onClick={()=>caluclate("/")}>/</button>
            <button onClick={()=>caluclate("x")}>X</button>

        </div>
{error || result &&(
       <Success message={error} result={result}/>
)}
       </>
  );
}

export default App;
