import logo from './logo.svg';
import './App.css';
import { Component, useState } from 'react';
import Success from "./success";

function App() {

  const [inputone,setinputone] = useState(0);
  const [inputtwo,setinputtwo] = useState(0);
  const [error,seterror] = useState(0);
  const [result,setresult] = useState(0);


  function caluclate(operation){

    if(inputone.trim()==""){
      seterror("input one is required");


    }
    else 
    if(inputtwo.trim()==""){
      seterror("input two is required");

    }
    else {

      switch(operation){
        case "+": setresult(inputone+inputtwo);
        case "-": setresult(inputone-inputtwo);
        case "/": setresult(inputone/inputtwo);
        case "X": setresult(inputone*inputtwo);
      }

    }

  }
  return (
    <>
    <div id="main">
            <input type="text" placeholder='Enter first number' onChange={(e)=>setinputone(Number(e.target.value))} />
            <input type="text" placeholder='Enter second number' onChange={(e)=>setinputtwo(Number(e.target.value))}/>
            <button onClick={()=>caluclate("+")}>+</button>
            <button onClick={()=>caluclate("-")}>-</button>
            <button onClick={()=>caluclate("/")}>/</button>
            <button onClick={()=>caluclate("/")}>X</button>

        </div>
{error || result &&(
       <Success message={error} result={result}/>
)}
       </>
  );
}

export default App;
